package lessons;

public class Lesson_11_condition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int o=10;
		if(o==10) {
			System.out.println("condition is true");
		}
		if(true) {
			System.out.println("condition is true");
		}
		if(false) {
			System.out.println("condition is true");
		}else {
			System.out.println("im else part--------");
		}
	}

}
